import api from './api';

const candidateService = {
  // Get all candidates
  getAllCandidates: async () => {
    try {
      const response = await api.get('/candidates');
      return response.data;
    } catch (error) {
      throw error.response?.data || { message: 'Failed to fetch candidates', success: false };
    }
  },

  // Add a new candidate
  addCandidate: async (candidateData) => {
    try {
      const response = await api.post('/candidates', candidateData);
      return response.data;
    } catch (error) {
      throw error.response?.data || { message: 'Failed to add candidate', success: false };
    }
  },

  // Update a candidate
  updateCandidate: async (id, candidateData) => {
    try {
      const response = await api.put(`/candidates/${id}`, candidateData);
      return response.data;
    } catch (error) {
      throw error.response?.data || { message: 'Failed to update candidate', success: false };
    }
  },

  // Delete a candidate
  deleteCandidate: async (id) => {
    try {
      const response = await api.delete(`/candidates/${id}`);
      return response.data;
    } catch (error) {
      throw error.response?.data || { message: 'Failed to delete candidate', success: false };
    }
  }
};

export default candidateService;
