import React, { useState, useRef, useEffect } from 'react';
import { Link } from 'react-router-dom';
import '../styles/Candidates.css';
import { Sidebar, ContentHeader } from '../components/common';
import candidateService from '../services/candidateService';

const Candidates = () => {
  const [searchQuery, setSearchQuery] = useState('');
  const [statusFilter, setStatusFilter] = useState('');
  const [positionFilter, setPositionFilter] = useState('');
  const [candidates, setCandidates] = useState([]);
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState('');

  const [activeDropdown, setActiveDropdown] = useState(null);
  const [showProfileDropdown, setShowProfileDropdown] = useState(false);
  const [showStatusDropdown, setShowStatusDropdown] = useState(false);
  const [showPositionDropdown, setShowPositionDropdown] = useState(false);
  const [showAddCandidateModal, setShowAddCandidateModal] = useState(false);
  const [newCandidateData, setNewCandidateData] = useState({
    name: '',
    email: '',
    phoneNumber: '',
    position: '',
    experience: 0,
    status: 'New'
  });
  const profileDropdownRef = useRef(null);
  const statusDropdownRef = useRef(null);
  const positionDropdownRef = useRef(null);
  const [profileStatus, setProfileStatus] = useState("Inactive");
  const [hoveredItem, setHoveredItem] = useState(null);
  const statusOptions = ["New", "Scheduled", "Ongoing", "Selected", "Rejected"];
  const positionOptions = ["Designer", "Developer", "Human Resource"];
  const [editingStatusId, setEditingStatusId] = useState(null);
  const [dropdownPosition, setDropdownPosition] = useState('bottom');

  // Fetch candidates from the backend
  useEffect(() => {
    fetchCandidates();
  }, []);

  const fetchCandidates = async () => {
    try {
      setLoading(true);
      setError('');
      const response = await candidateService.getAllCandidates();
      if (response.success) {
        setCandidates(response.data);
      } else {
        setError('Failed to fetch candidates');
      }
    } catch (err) {
      setError('Error fetching candidates: ' + (err.message || 'Unknown error'));
      console.error('Error fetching candidates:', err);
    } finally {
      setLoading(false);
    }
  };

  const handleActionClick = (id, e) => {
    if (activeDropdown === id) {
      setActiveDropdown(null);
    } else {
      setActiveDropdown(id);

      const buttonRect = e.currentTarget.getBoundingClientRect();
      const windowHeight = window.innerHeight;

      if (windowHeight - buttonRect.bottom < 200) {
        setDropdownPosition('top');
      } else {
        setDropdownPosition('bottom');
      }
    }
  };

  const getStatusClass = (status) => {
    switch(status) {
      case 'New':
        return 'status-new';
      case 'Scheduled':
        return 'status-scheduled';
      case 'Ongoing':
        return 'status-ongoing';
      case 'Selected':
        return 'status-selected';
      case 'Rejected':
        return 'status-rejected';
      default:
        return '';
    }
  };

  // Close the dropdowns when clicking outside
  useEffect(() => {
    function handleClickOutside(event) {
      if (profileDropdownRef.current && !profileDropdownRef.current.contains(event.target)) {
        setShowProfileDropdown(false);
      }
      if (statusDropdownRef.current && !statusDropdownRef.current.contains(event.target)) {
        setShowStatusDropdown(false);
      }
      if (positionDropdownRef.current && !positionDropdownRef.current.contains(event.target)) {
        setShowPositionDropdown(false);
      }
      if (activeDropdown && !event.target.closest('.dropdown-content') && !event.target.classList.contains('action-button')) {
        setActiveDropdown(null);
      }
    }

    document.addEventListener("mousedown", handleClickOutside);
    return () => {
      document.removeEventListener("mousedown", handleClickOutside);
    };
  }, [activeDropdown]);

  // Handle input change for the add candidate form
  const handleInputChange = (e) => {
    const { name, value, type, checked } = e.target;
    setNewCandidateData(prev => ({
      ...prev,
      [name]: type === 'checkbox' ? checked : value
    }));
  };

  // Handle form submission for adding a new candidate
  const handleAddCandidate = async (e) => {
    e.preventDefault();

    try {
      setLoading(true);
      setError('');

      const candidateData = {
        name: newCandidateData.name,
        email: newCandidateData.email,
        phoneNumber: newCandidateData.phoneNumber,
        position: newCandidateData.position,
        experience: parseInt(newCandidateData.experience, 10) || 0,
        status: 'New'
      };

      const response = await candidateService.addCandidate(candidateData);

      if (response.success) {
        // Reset form and close modal
        setNewCandidateData({
          name: '',
          email: '',
          phoneNumber: '',
          position: '',
          experience: 0,
          status: 'New'
        });

        setShowAddCandidateModal(false);

        // Refresh the candidate list
        fetchCandidates();
      } else {
        setError(response.message || 'Failed to add candidate');
      }
    } catch (err) {
      setError('Error adding candidate: ' + (err.message || 'Unknown error'));
      console.error('Error adding candidate:', err);
    } finally {
      setLoading(false);
    }
  };

  // Handle status change
  const handleStatusChange = async (candidateId, newStatus) => {
    try {
      setLoading(true);
      setError('');

      const response = await candidateService.updateCandidate(candidateId, { status: newStatus });

      if (response.success) {
        setCandidates(prev => 
          prev.map(candidate => 
            candidate._id === candidateId 
              ? { ...candidate, status: newStatus } 
              : candidate
          )
        );
      } else {
        setError(response.message || 'Failed to update candidate status');
      }
    } catch (err) {
      setError('Error updating candidate status: ' + (err.message || 'Unknown error'));
      console.error('Error updating candidate status:', err);
    } finally {
      setLoading(false);
      setEditingStatusId(null);
    }
  };

  // Handle candidate deletion
  const handleDeleteCandidate = async (candidateId) => {
    try {
      setLoading(true);
      setError('');

      const response = await candidateService.deleteCandidate(candidateId);

      if (response.success) {
        // Remove candidate from local state
        setCandidates(prev => prev.filter(candidate => candidate._id !== candidateId));
      } else {
        setError(response.message || 'Failed to delete candidate');
      }
    } catch (err) {
      setError('Error deleting candidate: ' + (err.message || 'Unknown error'));
      console.error('Error deleting candidate:', err);
    } finally {
      setLoading(false);
      setActiveDropdown(null);
    }
  };

  // Filter candidates based on search query and filters
  const filteredCandidates = candidates.filter(candidate => {
    const matchesSearch = 
      candidate.name.toLowerCase().includes(searchQuery.toLowerCase()) ||
      candidate.email.toLowerCase().includes(searchQuery.toLowerCase()) ||
      candidate.phoneNumber?.toLowerCase().includes(searchQuery.toLowerCase()) ||
      candidate.position.toLowerCase().includes(searchQuery.toLowerCase());

    const matchesStatus = statusFilter === '' || candidate.status === statusFilter;
    const matchesPosition = positionFilter === '' || candidate.position.includes(positionFilter);

    return matchesSearch && matchesStatus && matchesPosition;
  });

  return (
    <div className="candidates-page">
      <Sidebar />
      <div className="candidates-content">
        <ContentHeader />
        <div className="candidates-main-content">
          <div className="candidates-header">
            <h1>Candidates</h1>
            <button 
              className="add-candidate-button" 
              onClick={() => setShowAddCandidateModal(true)}
            >
              + Add Candidate
            </button>
          </div>

          {error && <div className="error-message">{error}</div>}

          <div className="filter-row">
            <div className="search-container">
              <input
                type="text"
                placeholder="Search candidates..."
                value={searchQuery}
                onChange={(e) => setSearchQuery(e.target.value)}
                className="search-input"
              />
              <svg className="search-icon" width="24" height="24" viewBox="0 0 24 24" fill="none" xmlns="http://www.w3.org/2000/svg">
                <path d="M21 21L15 15M17 10C17 13.866 13.866 17 10 17C6.13401 17 3 13.866 3 10C3 6.13401 6.13401 3 10 3C13.866 3 17 6.13401 17 10Z" stroke="#667085" strokeWidth="2" strokeLinecap="round" strokeLinejoin="round"/>
              </svg>
            </div>

            <div className="filter-container" ref={statusDropdownRef}>
              <button 
                className="filter-button"
                onClick={() => setShowStatusDropdown(!showStatusDropdown)}
              >
                Status
                <svg width="20" height="20" viewBox="0 0 20 20" fill="none" xmlns="http://www.w3.org/2000/svg">
                  <path d="M5 7.5L10 12.5L15 7.5" stroke="#667085" strokeWidth="1.66667" strokeLinecap="round" strokeLinejoin="round"/>
                </svg>
              </button>

              {showStatusDropdown && (
                <div className="dropdown-content filter-dropdown">
                  <div 
                    className={`dropdown-item ${statusFilter === '' ? 'active' : ''}`}
                    onClick={() => {
                      setStatusFilter('');
                      setShowStatusDropdown(false);
                    }}
                  >
                    All
                  </div>
                  {statusOptions.map((status, index) => (
                    <div 
                      key={index}
                      className={`dropdown-item ${statusFilter === status ? 'active' : ''}`}
                      onClick={() => {
                        setStatusFilter(status);
                        setShowStatusDropdown(false);
                      }}
                    >
                      {status}
                    </div>
                  ))}
                </div>
              )}
            </div>

            <div className="filter-container" ref={positionDropdownRef}>
              <button 
                className="filter-button"
                onClick={() => setShowPositionDropdown(!showPositionDropdown)}
              >
                Position
                <svg width="20" height="20" viewBox="0 0 20 20" fill="none" xmlns="http://www.w3.org/2000/svg">
                  <path d="M5 7.5L10 12.5L15 7.5" stroke="#667085" strokeWidth="1.66667" strokeLinecap="round" strokeLinejoin="round"/>
                </svg>
              </button>

              {showPositionDropdown && (
                <div className="dropdown-content filter-dropdown">
                  <div 
                    className={`dropdown-item ${positionFilter === '' ? 'active' : ''}`}
                    onClick={() => {
                      setPositionFilter('');
                      setShowPositionDropdown(false);
                    }}
                  >
                    All
                  </div>
                  {positionOptions.map((position, index) => (
                    <div 
                      key={index}
                      className={`dropdown-item ${positionFilter === position ? 'active' : ''}`}
                      onClick={() => {
                        setPositionFilter(position);
                        setShowPositionDropdown(false);
                      }}
                    >
                      {position}
                    </div>
                  ))}
                </div>
              )}
            </div>
          </div>

          {loading ? (
            <div className="loading-indicator">Loading candidates...</div>
          ) : (
            <div className="candidates-table">
              <table>
                <thead>
                  <tr>
                    <th>Name</th>
                    <th>Email</th>
                    <th>Phone number</th>
                    <th>Position</th>
                    <th>Status</th>
                    <th>Experience</th>
                    <th>Action</th>
                  </tr>
                </thead>
                <tbody>
                  {filteredCandidates.length > 0 ? (
                    filteredCandidates.map((candidate) => (
                      <tr key={candidate._id}>
                        <td>
                          <div className="candidate-name">
                            <div className="candidate-avatar">
                              {candidate.name.charAt(0).toUpperCase()}
                            </div>
                            {candidate.name}
                          </div>
                        </td>
                        <td>{candidate.email}</td>
                        <td>{candidate.phoneNumber}</td>
                        <td>{candidate.position}</td>
                        <td>
                          {editingStatusId === candidate._id ? (
                            <div className="status-dropdown">
                              <select 
                                value={candidate.status}
                                onChange={(e) => handleStatusChange(candidate._id, e.target.value)}
                                onBlur={() => setEditingStatusId(null)}
                                autoFocus
                              >
                                {statusOptions.map((status, idx) => (
                                  <option key={idx} value={status}>{status}</option>
                                ))}
                              </select>
                            </div>
                          ) : (
                            <div 
                              className={`status-badge ${getStatusClass(candidate.status)}`}
                              onClick={() => setEditingStatusId(candidate._id)}
                            >
                              {candidate.status}
                            </div>
                          )}
                        </td>
                        <td>{candidate.experience} {candidate.experience === 1 ? 'year' : 'years'}</td>
                        <td>
                          <div className="action-dropdown">
                            <button 
                              className="action-button" 
                              onClick={(e) => handleActionClick(candidate._id, e)}
                            >
                              <svg width="24" height="24" viewBox="0 0 24 24" fill="none" xmlns="http://www.w3.org/2000/svg">
                                <path d="M12 12V12.01M12 6V6.01M12 18V18.01" stroke="#667085" strokeWidth="2" strokeLinecap="round" strokeLinejoin="round"/>
                              </svg>
                            </button>

                            {activeDropdown === candidate._id && (
                              <div className={`dropdown-content action-dropdown-content ${dropdownPosition}`}>
                                <div className="dropdown-item" onClick={() => window.open(`mailto:${candidate.email}`, '_blank')}>
                                  <svg width="20" height="20" viewBox="0 0 20 20" fill="none" xmlns="http://www.w3.org/2000/svg">
                                    <path d="M18.3333 5L10 10.8333L1.66667 5M3.33333 3.33333H16.6667C17.5871 3.33333 18.3333 4.07953 18.3333 5V15C18.3333 15.9205 17.5871 16.6667 16.6667 16.6667H3.33333C2.41286 16.6667 1.66667 15.9205 1.66667 15V5C1.66667 4.07953 2.41286 3.33333 3.33333 3.33333Z" stroke="#344054" strokeWidth="1.66667" strokeLinecap="round" strokeLinejoin="round"/>
                                  </svg>
                                  Send Email
                                </div>
                                <div className="dropdown-item" onClick={() => setEditingStatusId(candidate._id)}>
                                  <svg width="20" height="20" viewBox="0 0 20 20" fill="none" xmlns="http://www.w3.org/2000/svg">
                                    <path d="M10 16.6667C13.6819 16.6667 16.6667 13.6819 16.6667 10C16.6667 6.3181 13.6819 3.33333 10 3.33333C6.3181 3.33333 3.33333 6.3181 3.33333 10C3.33333 13.6819 6.3181 16.6667 10 16.6667Z" stroke="#344054" strokeWidth="1.66667" strokeLinecap="round" strokeLinejoin="round"/>
                                    <path d="M10 6.66667V10L12.5 11.6667" stroke="#344054" strokeWidth="1.66667" strokeLinecap="round" strokeLinejoin="round"/>
                                  </svg>
                                  Change Status
                                </div>
                                <div className="dropdown-item" onClick={() => handleDeleteCandidate(candidate._id)}>
                                  <svg width="20" height="20" viewBox="0 0 20 20" fill="none" xmlns="http://www.w3.org/2000/svg">
                                    <path d="M2.5 5H4.16667M4.16667 5H17.5M4.16667 5V16.6667C4.16667 17.1087 4.34226 17.5326 4.65482 17.8452C4.96738 18.1577 5.39131 18.3333 5.83333 18.3333H14.1667C14.6087 18.3333 15.0326 18.1577 15.3452 17.8452C15.6577 17.5326 15.8333 17.1087 15.8333 16.6667V5H4.16667ZM6.66667 5V3.33333C6.66667 2.89131 6.84226 2.46738 7.15482 2.15482C7.46738 1.84226 7.89131 1.66667 8.33333 1.66667H11.6667C12.1087 1.66667 12.5326 1.84226 12.8452 2.15482C13.1577 2.46738 13.3333 2.89131 13.3333 3.33333V5M8.33333 9.16667V14.1667M11.6667 9.16667V14.1667" stroke="#344054" strokeWidth="1.66667" strokeLinecap="round" strokeLinejoin="round"/>
                                  </svg>
                                  Delete
                                </div>
                              </div>
                            )}
                          </div>
                        </td>
                      </tr>
                    ))
                  ) : (
                    <tr>
                      <td colSpan="7" className="no-candidates">
                        {searchQuery || statusFilter || positionFilter ? 
                          'No candidates match your search criteria' : 
                          'No candidates found. Add a new candidate to get started.'}
                      </td>
                    </tr>
                  )}
                </tbody>
              </table>
            </div>
          )}
        </div>
      </div>

      {/* Add Candidate Modal */}
      {showAddCandidateModal && (
        <div className="modal-overlay">
          <div className="modal add-candidate-modal">
            <div className="modal-header">
              <h2>Add New Candidate</h2>
              <button 
                className="close-button"
                onClick={() => setShowAddCandidateModal(false)}
              >
                <svg width="24" height="24" viewBox="0 0 24 24" fill="none" xmlns="http://www.w3.org/2000/svg">
                  <path d="M18 6L6 18M6 6L18 18" stroke="#667085" strokeWidth="2" strokeLinecap="round" strokeLinejoin="round"/>
                </svg>
              </button>
            </div>

            <form onSubmit={handleAddCandidate} className="add-candidate-form">
              <div className="form-group">
                <label htmlFor="name">Full Name*</label>
                <input
                  type="text"
                  id="name"
                  name="name"
                  value={newCandidateData.name}
                  onChange={handleInputChange}
                  placeholder="Enter candidate's full name"
                  required
                  disabled={loading}
                />
              </div>

              <div className="form-group">
                <label htmlFor="email">Email Address*</label>
                <input
                  type="email"
                  id="email"
                  name="email"
                  value={newCandidateData.email}
                  onChange={handleInputChange}
                  placeholder="Enter email address"
                  required
                  disabled={loading}
                />
              </div>

              <div className="form-group">
                <label htmlFor="phoneNumber">Phone Number*</label>
                <input
                  type="tel"
                  id="phoneNumber"
                  name="phoneNumber"
                  value={newCandidateData.phoneNumber}
                  onChange={handleInputChange}
                  placeholder="Enter phone number"
                  required
                  disabled={loading}
                />
              </div>

              <div className="form-group">
                <label htmlFor="position">Position*</label>
                <select
                  id="position"
                  name="position"
                  value={newCandidateData.position}
                  onChange={handleInputChange}
                  required
                  disabled={loading}
                >
                  <option value="">Select a position</option>
                  {positionOptions.map((position, index) => (
                    <option key={index} value={position}>{position}</option>
                  ))}
                </select>
              </div>

              <div className="form-group">
                <label htmlFor="experience">Experience (years)*</label>
                <input
                  type="number"
                  id="experience"
                  name="experience"
                  value={newCandidateData.experience}
                  onChange={handleInputChange}
                  min="0"
                  placeholder="Years of experience"
                  required
                  disabled={loading}
                />
              </div>

              <div className="form-actions">
                <button
                  type="button"
                  className="cancel-button"
                  onClick={() => setShowAddCandidateModal(false)}
                  disabled={loading}
                >
                  Cancel
                </button>
                <button
                  type="submit"
                  className="submit-button"
                  disabled={loading}
                >
                  {loading ? 'Adding...' : 'Add Candidate'}
                </button>
              </div>
            </form>
          </div>
        </div>
      )}
    </div>
  );
};

export default Candidates;
